import {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : [],
      filteredMonsters: []
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users/')
    .then(x => x.json())
    .then(y => this.setState(()=>{
      return {monsters: y}
    }, ()=>{
      console.log(this.state.monsters)
    }))
  }

  render(){
    return (
      <div className="App">
      <form>
        <input className='search-box' type="search" placeholder='search monsters' onChange={(event) => {

          if(event.target.value.length !== 0){
            let filteredMonsters = this.state.monsters.filter(monster => monster.name.toLowerCase().includes(event.target.value.toLowerCase()) )
            this.setState({filteredMonsters: filteredMonsters})
          }

        }}></input>
      </form>

        {
          this.state.monsters.map((monster) => {
            return <h2 key={monster.id}>{monster.name}</h2>
          })
        }

      </div>
    );
  }

}

export default App;
