import {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import {CardList} from './components/card-list/card-list-component';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monsters : [],
      displayMonsters: []
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users/')
    .then(x => x.json())
    .then(y => this.setState(()=>{
      return {monsters: y, displayMonsters: y}
    }, ()=>{
      console.log(this.state.monsters)
    }))
  }


  onSearchChange = (event) => {
    if(event.target.value.length !== 0){
      let filteredMonsters = this.state.monsters.filter(monster => monster.name.toLowerCase().includes(event.target.value.toLowerCase()) )
      this.setState({displayMonsters: filteredMonsters})
    }else{
      this.setState({displayMonsters: this.state.monsters})
    }
  }

  render(){

    const {displayMonsters} = this.state
    const {onSearchChange} = this

    return (
      <div className="App">
      <form>
        <input className='search-box' type="search" placeholder='search monsters' onChange={onSearchChange}></input>
      </form>

        {
          displayMonsters.map((monster) => {
            return <h2 key={monster.id}>{monster.name}</h2>
          })
        }

        <CardList></CardList>

      </div>
    );
  }

}

export default App;
